mapper.update:
UPDATE user
SET updated_at = NOW()

    ,avator = #{avator}


    ,nick = #{nick}


    ,email = #{email}


    ,phone = #{phone}


    ,address = #{address}

WHERE username = #{username}

UPDATE user
SET updated_at = NOW(), extension = JSON_MERGE_PATCH(IFNULL(extension, '{}'), #{extension})
WHERE username = #{username}

UPDATE user
SET updated_at = NOW(), password = #{password}
WHERE username = #{username}

UPDATE user
SET updated_at = NOW(), role = #{role}
WHERE username = #{username}
